      {
        "src": "hello.hsp",
        "main": true,
        "text": "<!--\nedit me!\n-->\n\n<template hello(name)>\n    <div class=\"msg\">\n        Hello {name}!\n    </div>\n</template>\n\n<script>\n// Needed by the playground application.\n// Update it, but do not remove it!\nmodule.exports = {\n  template: hello,\n  data: [\"World\"]\n};\n</script>"
      }
